mapper.select:
WITH ipcode AS (
) SELECT ip.ip_sn AS "ipSn", ip.ip AS "ip", ip.ip_desc AS "ipDesc", ip."codeGroupId" AS "codeGroupId", ip."detailCode" AS "detailCode", ip."detailCodeName" AS "detailCodeName", ROW_NUMBER() OVER( ORDER BY ip.ip_sn ) AS rn FROM ( SELECT ip.ip_sn, ip.ip, ip.ip_desc, c."codeGroupId", c."detailCode", c."detailCodeName", ROW_NUMBER() OVER( ORDER BY ip.ip_sn ) AS rn FROM ipinfo ip LEFT JOIN resipmapping rim ON ip.ip_sn = rim.ip_sn LEFT JOIN resinfo r ON r.res_serial_id = rim.res_serial_id LEFT JOIN ipcode c ON c."detailCode" = rim.ip_type_code
ORDER BY ip.ip_sn ) ip
